import React from 'react'
import './TodoItem.css'
import { BsCheckLg } from 'react-icons/bs';
import { AiFillDelete } from 'react-icons/ai';

function TodoItem(props) {
    const onComplete = () => {
        alert('TODO completado: ' + props.text)
    }

    const onDelete = () => {
        alert('TODO eliminado: ' + props.text)
    }

    return (
        <li className={`TodoItem ${props.completed && 'TodoItem--complete'}`}>
            <span className={`Icon Icon-check ${props.completed && 'Icon-check--active'}`} onClick={onComplete}><BsCheckLg /></span>
            <p className={`TodoItem-p ${props.completed && 'TodoItem-p--complete'}`}>{props.text}</p>
            <span className='Icon Icon-delete' onClick={onDelete}><AiFillDelete /></span>
        </li>
    )
}

export { TodoItem }
